import {useEffect, useState} from "react";

function App() {
    const [loading, setLoading] = useState(true);
    const [movies, setMovies] = useState([]);

    const getMovies = async() => {
        const json = await (
            await fetch(
                'https://yts.mx/api/v2/list_movies.json?minimum_rating=9.5&sort_by=year'
            )
        ).json();
        setMovies(json.data.movies);
        setLoading(false);

    }
    useEffect(() => {
        getMovies();
    }, [])
    const no_summary = "It looks like we don't have a Synopsis for this title yet. Be the first to contribute! Just click the \"Edit page\" button at the bottom of the page or learn more in the Synopsis submission guide.";
    console.log(movies);
    return (
        <div>
            {loading ?
                <h1>Loading...</h1>
                : <div>{movies.map((movie) =>
                    <div key={movie.id}>
                        <h2>{movie.title}</h2>
                        <img src={movie.medium_cover_image} alt="movie_img"/>
                        <p>{movie.summary !== no_summary ? movie.summary : "No Imformation"}</p>
                        <ul>
                            {movie.genres && movie.genres.map((genre) =>
                                <li key={genre}>{genre}</li>)
                            }
                        </ul>
                    </div>)}
                </div>}
        </div>
    );
}

export default App;
