import Movies from "../components/Movies";
import {useEffect, useState} from "react";
import styles from "../css/styles_Home.module.css";


function Home(){
    const [loading, setLoading] = useState(true);
    const [movies, setMovies] = useState([]);
    const [pages, setPages] = useState(1);

    const getMovies = async() => {
        const json = await (
            await fetch(
                `https://yts.mx/api/v2/list_movies.json?minimum_rating=8.0&sort_by=rating&limit=18&page=${pages}`
            )
        ).json();
        setMovies(json.data.movies);
        setLoading(false);

    }
    useEffect(() => {
        getMovies();
    }, [])
    console.log(movies);
    return(
        <div className={styles.home}>
            <div className={styles.title}>
                SC Movie
            </div>
            <div className={styles.list}>
                {loading ?
                    <h1>Loading...</h1>
                    : <div>{movies.map((movie) =>
                        <Movies
                            key={movie.id}
                            id = {movie.id}
                            coverImg={movie.medium_cover_image}
                            title={movie.title}
                            summary={movie.summary}
                            genres={movie.genres}
                        />
                    )}
                    </div>}
            </div>
            <div className={styles.by}>
                <span>by<a href={"https://github.com/minseoky/ToDoList-powered_by_react"} rel="noopener noreferrer" target="_blank">minseoky<img alt={"github mark"} className={styles.github} src="https://cdn-icons-png.flaticon.com/512/25/25231.png"></img></a></span>
            </div>
        </div>
    )
}

export default Home;
